INSERT INTO `ims_wechat_news` VALUES ('36', '3', '60', 'XTIhlKzVk_tUW8uV8R1hjiTiC9R_Bn4wHFJnXne56nM', 'http://mmbiz.qpic.cn/mmbiz_jpg/rnpvDkXEgCG5RDhJMW2CciaMReDh4TqRpfzdpC9KnjuU0Joqrkmw4ZL2ia27S9rGubsib3MibffelrSWHNiccgZVniaQ/0?wx_fmt=jpeg', '现行水价', '', '', '<p><img data-s=\"300,640\" data-type=\"png\" data-src=\"https://mmbiz.qpic.cn/mmbiz_png/rnpvDkXEgCERhKhAyxhf1mIlegOCrspDIxJL8YRmNYTauZcHts8zDh1kVLJy4NgciajyCdTJOUxaF1fOkUBXaXg/0?wx_fmt=png\" data-copyright=\"0\" style=\"\" class=\"\" data-ratio=\"0.3208092485549133\" data-w=\"692\"  /></p><p><img data-s=\"300,640\" data-type=\"png\" data-src=\"https://mmbiz.qpic.cn/mmbiz_png/rnpvDkXEgCERhKhAyxhf1mIlegOCrspD9RdeYtPxudw5q8fEm4Sm2yMEsRicFZbhJloLu3kwDoZab1ZjndMPkog/0?wx_fmt=png\" data-copyright=\"0\" style=\"\" class=\"\" data-ratio=\"0.47139303482587064\" data-w=\"804\"  /></p><p><br  /></p>', '', '0', 'http://mp.weixin.qq.com/s?__biz=MzAwNDY1MTAwMw==&mid=100000014&idx=1&sn=b1059b9fcf7b69712e8ae22daa9dd72d&chksm=1b29e39a2c5e6a8c86b8a83f7b28c96774f993812ae0971f1fa685aac144614d235541ae09ae#rd', '0', '0');
INSERT INTO `ims_wechat_news` VALUES ('37', '3', '61', 'XTIhlKzVk_tUW8uV8R1hjk82aVyTSZQ-N-N2eXAVN04', 'http://mmbiz.qpic.cn/mmbiz_jpg/rnpvDkXEgCG5RDhJMW2CciaMReDh4TqRpfzdpC9KnjuU0Joqrkmw4ZL2ia27S9rGubsib3MibffelrSWHNiccgZVniaQ/0?wx_fmt=jpeg', '内江市水务有限责任公司简介', '', '内江市水务有限责任公司成立于1958年，担负着内江城区的自来水供应和污水处理任务。内江水务始终践行“让政府放', '<p style=\"text-align: justify;line-height: 2em;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 内江市水务有限责任公司成立于1958年，担负着内江城区的自来水供应和污水处理任务。<br  />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 内江水务始终践行“让政府放心、让用户满意”的经营服务理念，忠实地履行为城市经济和人民生活服务的职能。经过几代水务人50余年的艰苦创业和奋力拼搏，内江水务已由最初的囤船取水发展为今天兼具供水排水的中型企业。公司现有水厂二座，拥有内江市污水处理有限责任公司、内江市豪盛管道安装有限责任公司、内江市豪鹏二次供水服务有限责任公司3个子公司，日供水能力15万立方米、污水处理能力10万立方米，固定资产约8亿元。公司先后获得“省级和谐企业”、“省级先进企业”、“省级文明单位”、“省职业道德建设先进单位”，国家建设部授予的“全国城建系统规范化服务竞赛活动优秀单位”和中国水协授予的“城镇供排水行业突出贡献单位”、“全国污水处理厂绩效评比优质运营单位”等一系列荣誉，为内江城市供排水事业实现跨越式发展做出积极贡献。<br  />&nbsp;&nbsp; &nbsp; &nbsp;&nbsp; “十三五”期间，内江水务将围绕内江建设“双百”城市的发展战略规划，加大供水基础设施建设，将投资10余亿元，建设内江市第二污水处理厂及配套管网、内江市第三自来水厂及配套引水管道、内江市应急供水抢险基地等重点供水排水项目，为内江经济建设助力。<br  />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 在新的历史起点，公司将一如既往，坚持“安全供水，优质服务”这一生产经营方针，求真务实，开拓创新，为实现国内一流供水企业的目标而不懈努力！新的机制，新的机遇，内江水务人将凭着勤奋与智慧，求实与探索，迎接新挑战，以非凡的业绩，为建设幸福美丽内江和经济腾飞作出新的贡献。<br  /></p>', '', '0', 'http://mp.weixin.qq.com/s?__biz=MzAwNDY1MTAwMw==&mid=100000012&idx=1&sn=1f13a08c9cf4c40fc61226ae193943a5&chksm=1b29e3982c5e6a8ede08ef66b46e52b4bcc430ebefdb0dcb03b8da55100ffef4a4c2b293ff30#rd', '0', '0');
