INSERT INTO `ims_wechat_news` VALUES ('39', '3', '63', 'XTIhlKzVk_tUW8uV8R1hjut9o4_yQRfuiwBq7tLWBF0', 'http://mmbiz.qpic.cn/mmbiz_jpg/rnpvDkXEgCG5RDhJMW2CciaMReDh4TqRpfl0Iicnyep1vZcu2VTnKsJ8VkvACXJnibh25zhwSrNibMKF7KZU1mDaGA/0?wx_fmt=jpeg', '服务指南', '', '1、抄表类信息：         抄表分为单月抄、双月抄、每月抄。每月1日至12日为查抄时间，用户应在水表查', '<p>1、抄表类信息： &nbsp;<br  /><br  />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 抄表分为单月抄、双月抄、每月抄。每月1日至12日为查抄时间，用户应在水表查抄后当月25日前到我司收费大厅结清水费，凡超过缴费期限的，供水企业按用户应缴水费每日加收千分之五的违约金；自催缴之日起至15日内仍不缴纳的，供水企业可对其暂停供水，待水费缴纳后恢复供水，恢复供水的费用由用户承担。凡采用总水表计量方式的用户，我司只查抄总水表水量，并按总水表水量计价收费，用户总表与分表水量差额部分由用户自行协商解决，我公司鼓励用户进行“一户一表”改造。<br  /><br  />2、水费缴纳信息： <br  /><br  />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 缴费方式有：现金缴费 、预存水费、代收业务（手机银行代收、银行ATM机代收、中国电信手机APP和营业厅代收、支付宝代收），请在抄表后当月25日前交纳水费，以免产生违约金。收费时间实行全月收费，周六周日不休息（国家法定假日另行通知）。<br  /><br  />3、用水用途信息： <br  /><br  />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 不同用水性质的用水应当单独安装结算水表。用水性质发生改变，须调整水价的用户，应填写书面申请，经现场核实后予以调整并从下月执行新价。<br  /><br  />4、用水计量信息： <br  /><br  />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 用户对水表计量提出异议的，可向质检机构或授权鉴定机构申请校验。经鉴定确认水表误差超过国家规定标准时，水费按误差比例多退少补。<br  /><br  />5、用水业务办理信息：<br  /><br  />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 用户办理用水申请或办理更名、过户及销户等业务，持相关证明资料（双方身份证复印件、房产证复印件或单位出具公章证明），到内江市水务有限责任公司客户服务部（玉溪路302号二楼服务大厅）提出书面申请，待审批后，对符合条件的申请，由客户服务部受理。<br  /><br  />供水服务热线电话：2192000&nbsp;&nbsp; 城 区 抄 表 组电话：2025090<br  /><br  />服务大厅咨询电话：2025168&nbsp;&nbsp; 东兴片区抄表组电话：2266029<br  /><br  />报 漏 抢 修 电话：2022307（二十四小时值班）<br  /><br  />智能水表服务电话：2036196&nbsp;&nbsp; 用水违章举报电话：2223107<br  /></p>', '', '0', 'http://mp.weixin.qq.com/s?__biz=MzAwNDY1MTAwMw==&mid=100000002&idx=1&sn=1f906d55d3078d9d52d3347993971c24&chksm=1b29e3962c5e6a806365faaf3c9d72a47237efc057ce6b7af37458c79e4a590a9de2f79c9018#rd', '0', '0');

